import React from 'react';
import axios from 'axios';

function Product() {

    const url = "https://5ece0f0d7c528e00167cda0f.mockapi.io/products/18"
    const [product, setProduct] = React.useState(null)

    React.useEffect(()=>{
        axios.get(url)
            .then(response => {
                setProduct(response.data)
            })    
    },[url])

    return(
        <div></div>
    )
}

export default Product;
