import React from 'react';
import './App.css';
import Toolbar from './components/Toolbar/Toolbar';
import SideDrawer from './components/SideDrawer/SideDrawer';
import Backdrop from './components/Backdrop/Backdrop';
import UseAxios from 'axios-hooks';

function App() {

  const [showMenu,setShowMenu] = React.useState(false)

  let mask;

  if(showMenu){
    mask = <Backdrop menuCloser={()=>setShowMenu(false)}/>
  }

  const [{data,loading,error},refetch] = UseAxios('https://5ece0f0d7c528e00167cda0f.mockapi.io/products/')

  if(loading){
    return(
      <div>
        <h1>Loading.........</h1>
      </div>
    )
  }
  if(error){
    return(
      <div>
        <h1>Error! Refer console.log()</h1>
        {console.log(error)}
      </div>
    )
  }

  return (
    <div>
      <Toolbar menuShower={()=>setShowMenu(true)} menuCloser={()=>setShowMenu(false)}/>
      <div style={{marginTop:'75px'}}>
        <button onClick={refetch}>refetch</button>
        {/* <pre>{JSON.stringify(data, null, 2)}</pre>       */}
        <h3>{data.name}</h3>
      </div>
      <SideDrawer show={showMenu} />
      {mask}
    </div>
  );
}

export default App;
